<?php  define('SITEROOT', 'http://localhost/pcrs/');     include_once("db.php");    /*---------------------------------------------------------    @name:    check_auth    @purpose: Ensure that the current session is authicated  ---------------------------------------------------------*/	function check_auth(){		session_name('PC_Repair_Shop');		session_start();		if( !isset($_SESSION['user_id']) ){			header('Location: '.SITEROOT.'auth.php');		}	}	  /*---------------------------------------------------------    @name:    update_last_access    @purpose: Updates a user's last access timestamp  ---------------------------------------------------------*/	function update_last_access(){		$domain = ( $_SESSION['access_level'] < 2 ) ? 'customers' : 'employees';		$id = $_SESSION[ 'user_id']; 		$db->runQuery("update $domain set last_access = CURRENT_TIMESTAMP where id = $id;");	}	  /*------------------------------------------------------------    @name:    get_header    @purpose: returns an access level aware navigation menu  ------------------------------------------------------------*/	function get_header(){		include('chunks/header.php');	}	  /*------------------------------------------------------------    @name:    get_footer    @purpose: returns the footer  ------------------------------------------------------------*/	function get_footer(){		echo '</div><div id="bottom">			&copy; 2010 PC Repair Shop			</div>			</body>			</html>';	}	  /*------------------------------------------------------------    @name:    get_msg    @purpose: display the user message   ------------------------------------------------------------*/  function get_msg(){    if(isset($_SESSION['msg'])) echo $_SESSION['msg'];    unset( $_SESSION['msg'] );  }    /*------------------------------------------------------------    @name:    mysql_to_date    @purpose: convert a mysql time stamp and possibly date to              the desired output format  ------------------------------------------------------------*/  function mysql_to_date( $str ){    return strftime('%a %d %b %Y', strtotime($str) );  }?>